	import SynchronousCommand from "../../ace/SynchronousCommand";
	import Event from "../../ace/Event";
	import RouteToQueryCardsAction from "../../../src/common/actions/RouteToQueryCardsAction";
	import RouteToAuthorViewAction from "../../../src/common/actions/RouteToAuthorViewAction";
	import * as AppUtils from "../../../src/AppUtils";
	import * as AppState from "../../../src/AppState";
	
	export default class AbstractBoxClickCommand extends SynchronousCommand {
	    constructor() {
	        super("box.BoxClickCommand");
	    }
	
	    initCommandData(data) {
	        data.outcomes = [];
	    }

		addQueryCardsOutcome(data) {
			data.outcomes.push("queryCards");
		}
		addAuthorViewOutcome(data) {
			data.outcomes.push("authorView");
		}
		
		publishEvents(data) {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("queryCards")) {
				events.push(new Event('box.BoxClickQueryCardsEvent'));
				actionsToBeTriggered.push(
					{
						action: new RouteToQueryCardsAction(), 
						data: {
							boxId: data.boxId
						}
					}
				);
			}
			if (data.outcomes.includes("authorView")) {
				events.push(new Event('box.BoxClickAuthorViewEvent'));
				actionsToBeTriggered.push(
					{
						action: new RouteToAuthorViewAction(), 
						data: {
							categoryId: data.categoryId, 
							reverse: data.reverse
						}
					}
				);
			}
			
			this.publish(events, data);
		  	AppState.stateUpdated();
			this.trigger(actionsToBeTriggered);
	
		}

	}
	
	
	
