import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/AppUtils";
import * as AppState from "../../../src/AppState";
import InitBoxesForDayDuringScoreAction from "../../../src/box/actions/InitBoxesForDayDuringScoreAction";

export default class AbstractScoreReinforceCardCommand extends AsynchronousCommand {
    constructor() {
        super("box.ScoreReinforceCardCommand");
    }
    
    initCommandData(data) {
        data.reinforceCardId = AppState.get(
        	["rootContainer", "mainView", "queryCardView", "nextCard", "reinforceCardId"]
        )
        ;
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}
	
	allMandatoryValuesAreSet(data) {
		if (!data.reinforceCardId) {
			console.warn("AbstractScoreReinforceCardCommand: reinforceCardId is mandatory but is not set", data);
			return false;
		}
		if (!data.keep) {
			console.warn("AbstractScoreReinforceCardCommand: keep is mandatory but is not set", data);
			return false;
		}
		return true;
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	if (this.allMandatoryValuesAreSet(data)) {
		    	let payload = {
		    		reinforceCardId : data.reinforceCardId,
		    		keep : data.keep
		    	};
				AppUtils.httpPost(
						`${AppUtils.settings.rootPath}/card/score-reinforce`, 
						data.uuid, 
						true,
						 payload)
					.then(() => {
						this.handleResponse(data, resolve, reject);
					}, (error) => {
						data.error = error;
						this.handleError(data, resolve, reject);
					})
					.catch(x => reject(x));
			} else {
				resolve(data);
			}
	    });
	}
	
	publishEvents(data) {
		return new Promise((resolve) => {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('box.ScoreReinforceCardOkEvent'));
				actionsToBeTriggered.push(
					{
						action: new InitBoxesForDayDuringScoreAction(), 
						data: {
						}
					}
				);
			}
			
			this.publish(events, data).then(() => {
		  		AppState.stateUpdated();
				this.trigger(actionsToBeTriggered).then(resolve);
			});
		})
	
	}

}



