import SynchronousCommand from "../../ace/SynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/app/AppUtils";

export default class AbstractEditCardCommand extends SynchronousCommand {
    constructor() {
        super("card.EditCardCommand");
    }

    initCommandData(data) {
        data.cardList = AppUtils.get(
        	["rootContainer", "mainView", "cardView", "cardList"]
        );
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}

    publishEvents(data) {
		if (data.outcomes.includes("ok")) {
			new Event('card.EditCardOkEvent').publish(data);
			AppUtils.stateUpdated();
		}
    }
}




