import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/AppUtils";
import * as AppState from "../../../src/AppState";

export default class AbstractLoadCardsCommand extends AsynchronousCommand {
    constructor() {
        super("card.LoadCardsCommand");
    }
    
    initCommandData(data) {
        data.selectedCategory = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "selectedCategory"]
        )
        ;
        data.naturalInputOrder = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "cardView", "naturalInputOrder"]
        )
        ;
        data.filterNonScheduled = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "filter", "filterNonScheduled", "value"]
        )
        ;
        data.priority = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "filter", "priority", "value"]
        )
        ;
        data.reverse = AppState.get(
        	["rootContainer", "mainView", "authorView", "reverse"]
        )
        ;
        data.selectedCardIds = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "cardView", "selectedCardIds"]
        )
        ;
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}
	
	allMandatoryValuesAreSet(data) {
		if (data.categoryId === undefined || data.categoryId === null) {
			console.warn("AbstractLoadCardsCommand: categoryId is mandatory but is not set", data);
			return false;
		}
		return true;
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	if (this.allMandatoryValuesAreSet(data)) {
				AppUtils.httpGet(
						`${AppUtils.settings.rootPath}/cards?${data.categoryId ? `categoryId=${data.categoryId}` : ""}&${data.filterNonScheduled ? `filterNonScheduled=${data.filterNonScheduled}` : ""}&${data.priority ? `priority=${data.priority}` : ""}&${data.reverse ? `reverse=${data.reverse}` : ""}`, 
						data.uuid, 
						true)
					.then((response) => {
						data.cardList = response.cardList;
						this.handleResponse(data, resolve, reject);
					}, (error) => {
						data.error = error;
						this.handleError(data, resolve, reject);
					})
					.catch(x => reject(x));
			} else {
				resolve(data);
			}
	    });
	}
	
	publishEvents(data) {
		return new Promise((resolve) => {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('card.LoadCardsOkEvent'));
			}
			
			this.publish(events, data).then(() => {
		  		AppState.stateUpdated();
				this.trigger(actionsToBeTriggered).then(resolve);
			});
		})
	
	}

}



