import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/AppUtils";
import * as AppState from "../../../src/AppState";
import LoadCardsAction from "../../../src/card/actions/LoadCardsAction";

export default class AbstractUpdateCardCommand extends AsynchronousCommand {
    constructor() {
        super("card.UpdateCardCommand");
    }
    
    initCommandData(data) {
        data.wanted = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "cardView", "editedCard", "wanted"]
        )
        ;
        data.given = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "cardView", "editedCard", "given"]
        )
        ;
        data.cardId = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "cardView", "editedCard", "cardId"]
        )
        ;
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}
	
	allMandatoryValuesAreSet(data) {
		if (data.cardId === undefined || data.cardId === null) {
			console.warn("AbstractUpdateCardCommand: cardId is mandatory but is not set", data);
			return false;
		}
		if (data.given === undefined || data.given === null) {
			console.warn("AbstractUpdateCardCommand: given is mandatory but is not set", data);
			return false;
		}
		if (data.wanted === undefined || data.wanted === null) {
			console.warn("AbstractUpdateCardCommand: wanted is mandatory but is not set", data);
			return false;
		}
		return true;
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	if (this.allMandatoryValuesAreSet(data)) {
		    	let payload = {
		    		cardId : data.cardId,
		    		given : data.given,
		    		wanted : data.wanted
		    	};
				AppUtils.httpPut(
						`${AppUtils.settings.rootPath}/card/update`, 
						data.uuid, 
						true,
						 payload)
					.then(() => {
						this.handleResponse(data, resolve, reject);
					}, (error) => {
						data.error = error;
						this.handleError(data, resolve, reject);
					})
					.catch(x => reject(x));
			} else {
				resolve(data);
			}
	    });
	}
	
	publishEvents(data) {
		return new Promise((resolve) => {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('card.UpdateCardOkEvent'));
				actionsToBeTriggered.push(
					{
						action: new LoadCardsAction(), 
						data: {
						}
					}
				);
			}
			
			this.publish(events, data).then(() => {
		  		AppState.stateUpdated();
				this.trigger(actionsToBeTriggered).then(resolve);
			});
		})
	
	}

}



