import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/AppUtils";
import * as AppState from "../../../src/AppState";
import ReloadCategoryTreeAction from "../../../src/category/actions/ReloadCategoryTreeAction";

export default class AbstractCreateCategoryCommand extends AsynchronousCommand {
    constructor() {
        super("category.CreateCategoryCommand");
    }
    
    initCommandData(data) {
        data.categoryName = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "categoryDialog", "categoryName"]
        )
        ;
        data.parentCategoryId = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "selectedCategory", "categoryId"]
        )
        ;
        data.rootCategoryId = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "rootCategory", "categoryId"]
        )
        ;
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}
	
	allMandatoryValuesAreSet(data) {
		if (data.categoryName === undefined || data.categoryName === null) {
			console.warn("AbstractCreateCategoryCommand: categoryName is mandatory but is not set", data);
			return false;
		}
		if (data.parentCategoryId === undefined || data.parentCategoryId === null) {
			console.warn("AbstractCreateCategoryCommand: parentCategoryId is mandatory but is not set", data);
			return false;
		}
		return true;
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	if (this.allMandatoryValuesAreSet(data)) {
		    	let payload = {
		    		categoryName : data.categoryName,
		    		parentCategoryId : data.parentCategoryId
		    	};
				AppUtils.httpPost(
						`${AppUtils.settings.rootPath}/category/create`, 
						data.uuid, 
						true,
						 payload)
					.then(() => {
						this.handleResponse(data, resolve, reject);
					}, (error) => {
						data.error = error;
						this.handleError(data, resolve, reject);
					})
					.catch(x => reject(x));
			} else {
				resolve(data);
			}
	    });
	}
	
	publishEvents(data) {
		return new Promise((resolve) => {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('category.CreateCategoryOkEvent'));
				actionsToBeTriggered.push(
					{
						action: new ReloadCategoryTreeAction(), 
						data: {
							selectedCategoryId: data.selectedCategoryId, 
							categoryIdToBeExpanded: data.categoryIdToBeExpanded
						}
					}
				);
			}
			
			this.publish(events, data).then(() => {
		  		AppState.stateUpdated();
				this.trigger(actionsToBeTriggered).then(resolve);
			});
		})
	
	}

}



