import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/AppUtils";
import * as AppState from "../../../src/AppState";

export default class AbstractInviteUserCommand extends AsynchronousCommand {
    constructor() {
        super("category.InviteUserCommand");
    }
    
    initCommandData(data) {
        data.categoryId = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "rootCategory", "categoryId"]
        )
        ;
        data.editable = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "inviteUserEditableDialog", "editable"]
        )
        ;
        data.invitedUsername = AppState.get(
        	["rootContainer", "mainView", "authorView", "categoryTree", "inviteUserEditableDialog", "invitedUsername"]
        )
        ;
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}
	
	allMandatoryValuesAreSet(data) {
		if (data.categoryId === undefined || data.categoryId === null) {
			console.warn("AbstractInviteUserCommand: categoryId is mandatory but is not set", data);
			return false;
		}
		if (data.invitedUsername === undefined || data.invitedUsername === null) {
			console.warn("AbstractInviteUserCommand: invitedUsername is mandatory but is not set", data);
			return false;
		}
		if (data.editable === undefined || data.editable === null) {
			console.warn("AbstractInviteUserCommand: editable is mandatory but is not set", data);
			return false;
		}
		return true;
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	if (this.allMandatoryValuesAreSet(data)) {
		    	let payload = {
		    		categoryId : data.categoryId,
		    		invitedUsername : data.invitedUsername,
		    		editable : data.editable
		    	};
				AppUtils.httpPut(
						`${AppUtils.settings.rootPath}/category/invite`, 
						data.uuid, 
						true,
						 payload)
					.then(() => {
						this.handleResponse(data, resolve, reject);
					}, (error) => {
						data.error = error;
						this.handleError(data, resolve, reject);
					})
					.catch(x => reject(x));
			} else {
				resolve(data);
			}
	    });
	}
	
	publishEvents(data) {
		return new Promise((resolve) => {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('category.InviteUserOkEvent'));
			}
			
			this.publish(events, data).then(() => {
		  		AppState.stateUpdated();
				this.trigger(actionsToBeTriggered).then(resolve);
			});
		})
	
	}

}



