import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/AppUtils";
import * as AppState from "../../../src/AppState";
import LoadCardsAction from "../../../src/card/actions/LoadCardsAction";

export default class AbstractLoadCategoryTreeCommand extends AsynchronousCommand {
    constructor() {
        super("category.LoadCategoryTreeCommand");
    }
    
    initCommandData(data) {
        data.reverse = AppState.get(
        	["rootContainer", "mainView", "authorView", "reverse"]
        )
        ;
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}
	
	allMandatoryValuesAreSet(data) {
		if (data.rootCategoryId === undefined || data.rootCategoryId === null) {
			console.warn("AbstractLoadCategoryTreeCommand: rootCategoryId is mandatory but is not set", data);
			return false;
		}
		return true;
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	if (this.allMandatoryValuesAreSet(data)) {
				AppUtils.httpGet(
						`${AppUtils.settings.rootPath}/category/tree?${data.rootCategoryId ? `rootCategoryId=${data.rootCategoryId}` : ""}&${data.filterNonScheduled ? `filterNonScheduled=${data.filterNonScheduled}` : ""}&${data.priority ? `priority=${data.priority}` : ""}&${data.reverse ? `reverse=${data.reverse}` : ""}`, 
						data.uuid, 
						true)
					.then((response) => {
						data.rootCategory = response.rootCategory;
						data.reverseBoxExists = response.reverseBoxExists;
						data.boxId = response.boxId;
						this.handleResponse(data, resolve, reject);
					}, (error) => {
						data.error = error;
						this.handleError(data, resolve, reject);
					})
					.catch(x => reject(x));
			} else {
				resolve(data);
			}
	    });
	}
	
	publishEvents(data) {
		return new Promise((resolve) => {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('category.LoadCategoryTreeOkEvent'));
				actionsToBeTriggered.push(
					{
						action: new LoadCardsAction(), 
						data: {
						}
					}
				);
			}
			
			this.publish(events, data).then(() => {
		  		AppState.stateUpdated();
				this.trigger(actionsToBeTriggered).then(resolve);
			});
		})
	
	}

}



