	import SynchronousCommand from "../../ace/SynchronousCommand";
	import Event from "../../ace/Event";
	import HideToastAction from "../../../src/common/actions/HideToastAction";
	import * as AppUtils from "../../../src/AppUtils";
	import * as AppState from "../../../src/AppState";
	
	export default class AbstractDisplayWarningToastCommand extends SynchronousCommand {
	    constructor() {
	        super("common.DisplayWarningToastCommand");
	    }
	
	    initCommandData(data) {
	        data.language = AppState.get(
	        	["rootContainer", "language"]
	        )
	        ;
	        data.messages = AppState.get(
	        	["rootContainer", "messages"]
	        )
	        ;
	        data.outcomes = [];
	    }

		addOkOutcome(data) {
			data.outcomes.push("ok");
		}
		
		publishEvents(data) {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('common.DisplayWarningToastOkEvent'));
				this.triggerWithDelay(new HideToastAction(), 
					{
						id: data.id
					},
					5000
				);
			}
			
			this.publish(events, data);
		  	AppState.stateUpdated();
			this.trigger(actionsToBeTriggered);
	
		}

	}
	
	
	
