	import SynchronousCommand from "../../ace/SynchronousCommand";
	import Event from "../../ace/Event";
	import GetUserInfoAction from "../../../src/common/actions/GetUserInfoAction";
	import RouteChangedAction from "../../../src/common/actions/RouteChangedAction";
	import * as AppUtils from "../../../src/AppUtils";
	import * as AppState from "../../../src/AppState";
	
	export default class AbstractInitCommand extends SynchronousCommand {
	    constructor() {
	        super("common.InitCommand");
	    }
	
	    initCommandData(data) {
	        data.token = AppState.getStorage(
	        	["rootContainer", "token"]
	        )
	        ;
	        data.outcomes = [];
	    }

		addOkOutcome(data) {
			data.outcomes.push("ok");
		}
		addUserOutcome(data) {
			data.outcomes.push("user");
		}
		addNoUserOutcome(data) {
			data.outcomes.push("noUser");
		}
		
		publishEvents(data) {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('common.InitOkEvent'));
			}
			if (data.outcomes.includes("user")) {
				events.push(new Event('common.InitUserEvent'));
				actionsToBeTriggered.push(
					{
						action: new GetUserInfoAction(), 
						data: {
							token: data.token
						}
					}
				);
			}
			if (data.outcomes.includes("noUser")) {
				events.push(new Event('common.InitNoUserEvent'));
				actionsToBeTriggered.push(
					{
						action: new RouteChangedAction(), 
						data: {
						}
					}
				);
			}
			
			this.publish(events, data);
		  	AppState.stateUpdated();
			this.trigger(actionsToBeTriggered);
	
		}

	}
	
	
	
