import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/AppUtils";
import * as AppState from "../../../src/AppState";
import DisplayToastAction from "../../../src/common/actions/DisplayToastAction";
import RouteToDefaultAction from "../../../src/common/actions/RouteToDefaultAction";
import DisplayErrorToastAction from "../../../src/common/actions/DisplayErrorToastAction";

export default class AbstractResetPasswordCommand extends AsynchronousCommand {
    constructor() {
        super("password.ResetPasswordCommand");
    }
    
    initCommandData(data) {
        data.token = AppState.get(
        	["rootContainer", "mainView", "resetPasswordView", "token"]
        )
        ;
        data.password = AppState.get(
        	["rootContainer", "mainView", "resetPasswordView", "password", "value"]
        )
        ;
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}
	addErrorOutcome(data) {
		data.outcomes.push("error");
	}
	
	allMandatoryValuesAreSet(data) {
		if (data.password === undefined || data.password === null) {
			console.warn("AbstractResetPasswordCommand: password is mandatory but is not set", data);
			return false;
		}
		if (data.token === undefined || data.token === null) {
			console.warn("AbstractResetPasswordCommand: token is mandatory but is not set", data);
			return false;
		}
		return true;
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	if (this.allMandatoryValuesAreSet(data)) {
		    	let payload = {
		    		password : data.password,
		    		token : data.token
		    	};
				AppUtils.httpPut(
						`${AppUtils.settings.rootPath}/users/resetpassword`, 
						data.uuid, 
						false,
						 payload)
					.then(() => {
						this.handleResponse(data, resolve, reject);
					}, (error) => {
						data.error = error;
						this.handleError(data, resolve, reject);
					})
					.catch(x => reject(x));
			} else {
				resolve(data);
			}
	    });
	}
	
	publishEvents(data) {
		return new Promise((resolve) => {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('password.ResetPasswordOkEvent'));
				actionsToBeTriggered.push(
					{
						action: new DisplayToastAction(), 
						data: {
							textKey: data.textKey, 
							args: data.args
						}
					}
				);
				actionsToBeTriggered.push(
					{
						action: new RouteToDefaultAction(), 
						data: {
						}
					}
				);
			}
			if (data.outcomes.includes("error")) {
				events.push(new Event('password.ResetPasswordErrorEvent'));
				actionsToBeTriggered.push(
					{
						action: new DisplayErrorToastAction(), 
						data: {
							textKey: data.textKey, 
							args: data.args
						}
					}
				);
				actionsToBeTriggered.push(
					{
						action: new RouteToDefaultAction(), 
						data: {
						}
					}
				);
			}
			
			this.publish(events, data).then(() => {
		  		AppState.stateUpdated();
				this.trigger(actionsToBeTriggered).then(resolve);
			});
		})
	
	}

}



