import AsynchronousCommand from "../../ace/AsynchronousCommand";
import Event from "../../ace/Event";
import * as AppUtils from "../../../src/AppUtils";
import * as AppState from "../../../src/AppState";
import RouteToDefaultAction from "../../../src/common/actions/RouteToDefaultAction";

export default class AbstractLoginCommand extends AsynchronousCommand {
    constructor() {
        super("registration.LoginCommand");
    }
    
    initCommandData(data) {
        data.username = AppState.get(
        	["rootContainer", "mainView", "registrationView", "username", "value"]
        )
        ;
        data.password = AppState.get(
        	["rootContainer", "mainView", "registrationView", "password", "value"]
        )
        ;
        data.outcomes = [];
    }

	addOkOutcome(data) {
		data.outcomes.push("ok");
	}

	execute(data) {
	    return new Promise((resolve, reject) => {
	    	let payload = {
	    		username : data.username,
	    		password : data.password
	    	};
			AppUtils.httpPut(
					`${AppUtils.settings.rootPath}/user/token`, 
					data.uuid, 
					false,
					 payload)
				.then((response) => {
					data.token = response.token;
					this.handleResponse(data, resolve, reject);
				}, (error) => {
					data.error = error;
					this.handleError(data, resolve, reject);
				})
				.catch(x => reject(x));
	    });
	}
	
	publishEvents(data) {
		return new Promise((resolve) => {
			const events = [];
			const actionsToBeTriggered = [];
			if (data.outcomes.includes("ok")) {
				events.push(new Event('registration.LoginOkEvent'));
				actionsToBeTriggered.push(
					{
						action: new RouteToDefaultAction(), 
						data: {
						}
					}
				);
			}
			
			this.publish(events, data).then(() => {
		  		AppState.stateUpdated();
				this.trigger(actionsToBeTriggered).then(resolve);
			});
		})
	
	}

}



